mapper.select:
/* kr.co.dbvision.api.mhs.hrm.mhshrm005.service.mapper.Mhshrm005Mapper.selectMhshrm005List */ SELECT A.ORGNZT_CODE, B.ORGNZT_NM, A.DEPT_CODE, C.DEPT_KOR_NM, FNC_DATESTR(A.USE_BEGIN_DE) AS USE_BEGIN_DE, FNC_DATESTR(A.USE_END_DE) AS USE_END_DE, A.USE_AT, A.SORT_ORDR, A.REG_DT, A.REG_ID, A.UPT_DT, A.UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_DEPT_ORGNZT A, MHS_ORGNZT B, MHS_DEPT C WHERE A.ORGNZT_CODE = B.ORGNZT_CODE AND A.DEPT_CODE = C.DEPT_CODE
AND A.ORGNZT_CODE = #{orgnztCode}
AND B.ORGNZT_NM LIKE '%' || #{orgnztNm} || '%'
AND A.DEPT_CODE = #{deptCode}
AND C.DEPT_KOR_NM LIKE '%' || #{deptKorNm} || '%'
AND A.USE_AT = #{useAt}
AND A.REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND A.REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')

/* kr.co.dbvision.api.mhs.hrm.mhshrm005.service.mapper.Mhshrm005Mapper.selectMhshrm005 */ SELECT ORGNZT_CODE, (SELECT ORGNZT_NM FROM MHS_ORGNZT WHERE ORGNZT_CODE=A.ORGNZT_CODE) AS ORGNZT_NM, DEPT_CODE, (SELECT DEPT_KOR_NM FROM MHS_DEPT WHERE DEPT_CODE=A.DEPT_CODE) AS DEPT_KOR_NM, USE_BEGIN_DE, USE_END_DE, USE_AT, SORT_ORDR, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_DEPT_ORGNZT A
AND ORGNZT_CODE = #{orgnztCode}
AND DEPT_CODE = #{deptCode}

        /* kr.co.dbvision.api.mhs.hrm.mhshrm005.service.mapper.Mhshrm005Mapper.deleteMhshrm005UseChk */
		SELECT NVL(SUM(X.CNT),0) AS CNT, MAX(X.TBL) AS TBL
		FROM (
	        SELECT 0 AS CNT , '준비중' AS TBL
	          FROM DUAL
	         WHERE #{orgnztCode} = #{orgnztCode}
	           AND #{deptCode} = #{deptCode}
        ) X
        WHERE X.CNT > 0

mapper.update:
/* kr.co.dbvision.api.mhs.hrm.mhshrm005.service.mapper.Mhshrm005Mapper.saveMhshrm005 */ MERGE INTO MHS_DEPT_ORGNZT A USING (SELECT #{orgnztCode} ORGNZT_CODE, #{deptCode} DEPT_CODE FROM DUAL) B ON ( A.ORGNZT_CODE = B.ORGNZT_CODE AND A.DEPT_CODE = B.DEPT_CODE ) WHEN MATCHED THEN UPDATE SET
USE_BEGIN_DE= REPLACE(#{useBeginDe},'-',''), USE_END_DE= REPLACE(#{useEndDe},'-',''), USE_AT= #{useAt}, SORT_ORDR= #{sortOrdr}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
ORGNZT_CODE, DEPT_CODE, USE_BEGIN_DE, USE_END_DE, USE_AT, SORT_ORDR, REG_DT, REG_ID
) VALUES (
#{orgnztCode}, #{deptCode}, REPLACE(#{useBeginDe},'-',''), REPLACE(#{useEndDe},'-',''), #{useAt}, #{sortOrdr}, SYSDATE, #{regId}
)

mapper.delete:
/* kr.co.dbvision.api.mhs.hrm.mhshrm005.service.mapper.Mhshrm005Mapper.deleteMhshrm005 */
DELETE FROM MHS_DEPT_ORGNZT


        AND ORGNZT_CODE = #{orgnztCode}
        AND DEPT_CODE = #{deptCode}
