mapper.select:
/* kr.co.dbvision.api.mhs.hrm.mhshrm006.service.mapper.Mhshrm006Mapper.selectMhshrm006 */ SELECT FAMILY_CODE, FAMILY_RELATE_NM, FAMILY_ALLWNC_AMT, USE_BEGIN_DE, USE_END_DE, USE_AT, SORT_ORDR, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_FAMILYCODE A
AND FAMILY_CODE = #{familyCode}

/* kr.co.dbvision.api.mhs.hrm.mhshrm006.service.mapper.Mhshrm006Mapper.insertMhshrm006UseChk */
SELECT COUNT(*) AS CNT
  FROM MHS_FAMILYCODE
 WHERE  FAMILY_CODE = #{familyCode}

        /* kr.co.dbvision.api.mhs.hrm.mhshrm006.service.mapper.Mhshrm006Mapper.deleteMhshrm006UseChk */
		SELECT NVL(SUM(X.CNT),0) AS CNT, MAX(X.TBL) AS TBL
		FROM (
	        SELECT COUNT(*) AS CNT , '가족' AS TBL
	          FROM MHS_FAMILY
	         WHERE FAMILY_CODE = #{familyCode}
        ) X
        WHERE X.CNT > 0

mapper.update:
/* kr.co.dbvision.api.mhs.hrm.mhshrm006.service.mapper.Mhshrm006Mapper.saveMhshrm006 */ MERGE INTO MHS_FAMILYCODE A USING (SELECT #{familyCode} FAMILY_CODE FROM DUAL) B ON ( A.FAMILY_CODE = B.FAMILY_CODE ) WHEN MATCHED THEN UPDATE SET
FAMILY_RELATE_NM= #{familyRelateNm}, FAMILY_ALLWNC_AMT= #{familyAllwncAmt}, USE_BEGIN_DE= REPLACE(#{useBeginDe},'-',''), USE_END_DE= REPLACE(#{useEndDe},'-',''), USE_AT= #{useAt}, SORT_ORDR= #{sortOrdr}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
FAMILY_CODE, FAMILY_RELATE_NM, FAMILY_ALLWNC_AMT, USE_BEGIN_DE, USE_END_DE, USE_AT, SORT_ORDR, REG_DT, REG_ID
) VALUES (
#{familyCode}, #{familyRelateNm}, #{familyAllwncAmt}, REPLACE(#{useBeginDe},'-',''), REPLACE(#{useEndDe},'-',''), #{useAt}, #{sortOrdr}, SYSDATE, #{regId}
)
