mapper.select:
/* kr.co.dbvision.api.mhs.hrm.mhshrm010.service.mapper.Mhshrm010Mapper.selectMhshrm010List */ SELECT BSRP_SE_CODE, CLSF_CODE, DAYCT_AMT, STAYNGCT_AMT, TRNSPORTCT_AMT, CGFFD_AMT, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_BSRPSTDR A
AND BSRP_SE_CODE = #{bsrpSeCode}
AND CLSF_CODE = #{clsfCode}
AND REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')
ORDER BY BSRP_SE_CODE,CLSF_CODE

/* kr.co.dbvision.api.mhs.hrm.mhshrm010.service.mapper.Mhshrm010Mapper.selectMhshrm010ListForExcel */ SELECT FNC_COMCODENM('C024',A.BSRP_SE_CODE) AS BSRP_SE_CODE, (SELECT B.CLSF_NM FROM MHS_CLSF_CODE B WHERE B.CLSF_CODE = A.CLSF_CODE) AS CLSF_CODE, DAYCT_AMT, STAYNGCT_AMT, TRNSPORTCT_AMT, CGFFD_AMT, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_BSRPSTDR A
AND BSRP_SE_CODE = #{bsrpSeCode}
AND CLSF_CODE = #{clsfCode}
AND REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')
ORDER BY BSRP_SE_CODE,CLSF_CODE

/* kr.co.dbvision.api.mhs.hrm.mhshrm010.service.mapper.Mhshrm010Mapper.selectMhshrm010 */ SELECT BSRP_SE_CODE, CLSF_CODE, DAYCT_AMT, STAYNGCT_AMT, TRNSPORTCT_AMT, CGFFD_AMT, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_BSRPSTDR A
AND BSRP_SE_CODE = #{bsrpSeCode}
AND CLSF_CODE = #{clsfCode}

        /* kr.co.dbvision.api.mhs.hrm.mhshrm010.service.mapper.Mhshrm010Mapper.deleteMhshrm010UseChk */
		SELECT NVL(SUM(X.CNT),0) AS CNT, MAX(X.TBL) AS TBL
		FROM (
	        SELECT 0 AS CNT , '준비중' AS TBL
	          FROM DUAL
	         WHERE #{bsrpSeCode} = #{bsrpSeCode}
	           AND #{clsfCode} = #{clsfCode}
        ) X
        WHERE X.CNT > 0

mapper.update:
/* kr.co.dbvision.api.mhs.hrm.mhshrm010.service.mapper.Mhshrm010Mapper.saveMhshrm010 */ MERGE INTO MHS_BSRPSTDR A USING (SELECT #{bsrpSeCode} BSRP_SE_CODE, #{clsfCode} CLSF_CODE FROM DUAL) B ON ( A.BSRP_SE_CODE = B.BSRP_SE_CODE AND A.CLSF_CODE = B.CLSF_CODE ) WHEN MATCHED THEN UPDATE SET
DAYCT_AMT= #{dayctAmt}, STAYNGCT_AMT= #{stayngctAmt}, TRNSPORTCT_AMT= #{trnsportctAmt}, CGFFD_AMT= #{cgffdAmt}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
BSRP_SE_CODE, CLSF_CODE, DAYCT_AMT, STAYNGCT_AMT, TRNSPORTCT_AMT, CGFFD_AMT, REG_DT, REG_ID
) VALUES (
#{bsrpSeCode}, #{clsfCode}, #{dayctAmt}, #{stayngctAmt}, #{trnsportctAmt}, #{cgffdAmt}, SYSDATE, #{regId}
)

mapper.delete:
/* kr.co.dbvision.api.mhs.hrm.mhshrm010.service.mapper.Mhshrm010Mapper.deleteMhshrm010 */
DELETE FROM MHS_BSRPSTDR


        AND BSRP_SE_CODE = #{bsrpSeCode}
        AND CLSF_CODE = #{clsfCode}
