mapper.select:
/* kr.co.dbvision.api.mhs.hrm.mhshrm013.service.mapper.Mhshrm013Mapper.selectMhshrm013List */ SELECT DCLZCODE, DCLZCODE_NM, ABSENC_SE_CODE, VAC_SE_CODE, ENNCAMT_AT, REG_DT, REG_ID, UPT_DT, UPT_ID, USE_AT, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_DCLZ_CODE A
AND DCLZCODE = #{dclzcode}
AND DCLZCODE_NM LIKE '%' || #{dclzcodeNm} || '%'
AND USE_AT = #{useAt}
AND REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')
ORDER BY DCLZCODE

/* kr.co.dbvision.api.mhs.hrm.mhshrm013.service.mapper.Mhshrm013Mapper.searchMhshrm013ForExcel */ SELECT DCLZCODE, DCLZCODE_NM, (SELECT CODE_KOR_NM FROM STM_CODE WHERE CODEKIND_CODE = 'C167' AND CODE = ABSENC_SE_CODE) AS ABSENC_SE_CODE, (SELECT CODE_KOR_NM FROM STM_CODE WHERE CODEKIND_CODE = 'C081' AND CODE = VAC_SE_CODE) AS VAC_SE_CODE, DECODE(ENNCAMT_AT, '1', '무급', '0', '유급', '무급') AS ENNCAMT_AT, REG_DT, REG_ID, UPT_DT, UPT_ID, DECODE(USE_AT, '1', '사용', '0', '미사용', '미사용') AS USE_AT, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_DCLZ_CODE A
AND DCLZCODE = #{dclzcode}
AND DCLZCODE_NM LIKE '%' || #{dclzcodeNm} || '%'
AND USE_AT = #{useAt}
AND REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')
ORDER BY DCLZCODE

/* kr.co.dbvision.api.mhs.hrm.mhshrm013.service.mapper.Mhshrm013Mapper.selectMhshrm013 */ SELECT DCLZCODE, DCLZCODE_NM, ABSENC_SE_CODE, VAC_SE_CODE, ENNCAMT_AT, REG_DT, REG_ID, UPT_DT, UPT_ID, USE_AT, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_DCLZ_CODE A
AND DCLZCODE = #{dclzcode}

mapper.update:
/* kr.co.dbvision.api.mhs.hrm.mhshrm013.service.mapper.Mhshrm013Mapper.saveMhshrm013 */ MERGE INTO MHS_DCLZ_CODE A USING (SELECT #{dclzcode} DCLZCODE FROM DUAL) B ON ( A.DCLZCODE = B.DCLZCODE ) WHEN MATCHED THEN UPDATE SET
DCLZCODE_NM= #{dclzcodeNm}, ABSENC_SE_CODE= #{absencSeCode}, VAC_SE_CODE= #{vacSeCode}, ENNCAMT_AT= #{enncamtAt}, USE_AT= #{useAt}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
DCLZCODE, DCLZCODE_NM, ABSENC_SE_CODE, VAC_SE_CODE, ENNCAMT_AT, USE_AT, REG_DT, REG_ID
) VALUES (
#{dclzcode}, #{dclzcodeNm}, #{absencSeCode}, #{vacSeCode}, #{enncamtAt}, #{useAt}, SYSDATE, #{regId}
)
