mapper.select:
/* kr.co.dbvision.api.mhs.hrm.mhshrm015.service.mapper.Mhshrm015Mapper.selectMhshrm015List */ SELECT OFCPS_CODE, OFCPS_NM, FNC_DATESTR(USE_BEGIN_DE) AS USE_BEGIN_DE, FNC_DATESTR(USE_END_DE) AS USE_END_DE, USE_AT, SORT_ORDR, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_OFCPS_CODE A
AND OFCPS_CODE = #{ofcpsCode}
AND OFCPS_NM LIKE '%' || #{ofcpsNm} || '%'
AND USE_AT = #{useAt}
AND REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')
ORDER BY SORT_ORDR

/* kr.co.dbvision.api.mhs.hrm.mhshrm015.service.mapper.Mhshrm015Mapper.selectMhshrm015ExcelList */ SELECT OFCPS_CODE, OFCPS_NM, FNC_DATESTR(USE_BEGIN_DE) AS USE_BEGIN_DE, FNC_DATESTR(USE_END_DE) AS USE_END_DE, DECODE(USE_AT, '1', '사용', '0', '미사용', '미사용') AS USE_AT, SORT_ORDR, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_OFCPS_CODE A
AND OFCPS_CODE = #{ofcpsCode}
AND OFCPS_NM LIKE '%' || #{ofcpsNm} || '%'
AND USE_AT = #{useAt}
AND REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')
ORDER BY SORT_ORDR

/* kr.co.dbvision.api.mhs.hrm.mhshrm015.service.mapper.Mhshrm015Mapper.selectMhshrm015 */ SELECT OFCPS_CODE, OFCPS_NM, USE_BEGIN_DE, USE_END_DE, USE_AT, SORT_ORDR, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_OFCPS_CODE A
AND OFCPS_CODE = #{ofcpsCode}

mapper.update:
/* kr.co.dbvision.api.mhs.hrm.mhshrm015.service.mapper.Mhshrm015Mapper.saveMhshrm015 */ MERGE INTO MHS_OFCPS_CODE A USING (SELECT #{ofcpsCode} OFCPS_CODE FROM DUAL) B ON ( A.OFCPS_CODE = B.OFCPS_CODE ) WHEN MATCHED THEN UPDATE SET
OFCPS_NM= #{ofcpsNm}, USE_BEGIN_DE= REPLACE(#{useBeginDe},'-',''), USE_END_DE= REPLACE(#{useEndDe},'-',''), USE_AT= #{useAt}, SORT_ORDR= #{sortOrdr}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
OFCPS_CODE, OFCPS_NM, USE_BEGIN_DE, USE_END_DE, USE_AT, SORT_ORDR, REG_DT, REG_ID
) VALUES (
#{ofcpsCode}, #{ofcpsNm}, REPLACE(#{useBeginDe},'-',''), REPLACE(#{useEndDe},'-',''), #{useAt}, #{sortOrdr}, SYSDATE, #{regId}
)

mapper.select:
/* kr.co.dbvision.api.mhs.hrm.mhshrm015.service.mapper.Mhshrm015Mapper.checkDeleteMhshrm015 */
SELECT
       B.EMPNO AS USE_CHECK,
       A.EMPNO AS USE_CHECK2

  FROM MHS_GNFD A, MHS_EMP B


    ROWNUM = 1

        AND (A.BFCHG_OFCPS_CODE = #{ofcpsCode}
            OR A.AFCHG_OFCPS_CODE = #{ofcpsCode}
            OR B.OFCPS_CODE = #{ofcpsCode})
