mapper.select:
/* kr.co.dbvision.api.mps.cal.mpscal016.service.mapper.Mpscal016Mapper.selectMpscal016List */ SELECT A.EMPNO, (SELECT KOR_NM FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS KOR_NM, (SELECT HFFS_SE FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS HFFS_SE, (SELECT CLSF_CODE FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS CLSF_CODE, (SELECT FNC_GETCLSFNM(CLSF_CODE) FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS CLSF_CODE_NM, (SELECT FNC_GETDEPTNM(DEPT_CODE) FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS DEPT_CODE_NM, FNC_COMCODENM('C062', A.SALARYTY_CODE) AS SALARYTY_CODE, A.SALARYITEM_CODE, (SELECT SALARYITEM_NM FROM MPS_SALARYITEM B WHERE A.SALARYITEM_CODE = B.SALARYITEM_CODE ) AS SALARYITEM_NM, A.APPLC_YM, (SELECT TO_CHAR((TO_DATE(PYMNT_DE,'YYYY-MM-DD')),'YYYY-MM-DD') FROM MPS_PYMNTDE B WHERE A.APPLC_YM = B.APPLC_YM AND A.PYMNT_SN = B.PYMNT_SN) AS PYMNT_DE, A.PYMNT_SN, A.PYMNT_AMT, A.RM, A.APPLC_AT
FROM MPS_SALARY_BNDE_REGIST A , MHS_EMP B
A.EMPNO = B.EMPNO
AND A.EMPNO = #{empno}
AND FNC_GETDEPTNM(B.DEPT_CODE) LIKE '%' || #{deptCode} || '%'
AND A.PYMNT_SN = #{pymntSn}
AND B.HFFS_SE = #{hffsSe}

/* kr.co.dbvision.api.mps.cal.mpscal016.service.mapper.Mpscal016Mapper.selectMpscal016 */ SELECT A.EMPNO, (SELECT KOR_NM FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS KOR_NM, (SELECT HFFS_SE FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS HFFS_SE, (SELECT CLSF_CODE FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS CLSF_CODE, (SELECT FNC_GETCLSFNM(CLSF_CODE) FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS CLSF_CODE_NM, (SELECT FNC_GETDEPTNM(DEPT_CODE) FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS DEPT_CODE_NM, A.SALARYTY_CODE, A.SALARYITEM_CODE, (SELECT SALARYITEM_NM FROM MPS_SALARYITEM B WHERE A.SALARYITEM_CODE = B.SALARYITEM_CODE ) AS SALARYITEM_NM, A.APPLC_YM, (SELECT TO_CHAR((TO_DATE(PYMNT_DE,'YYYY-MM-DD')),'YYYY-MM-DD') FROM MPS_PYMNTDE B WHERE A.APPLC_YM = B.APPLC_YM AND A.PYMNT_SN = B.PYMNT_SN) AS PYMNT_DE, A.PYMNT_SN, A.PYMNT_AMT, A.RM, A.APPLC_AT
FROM MPS_SALARY_BNDE_REGIST A
AND A.EMPNO = #{empno}
AND A.DEPT_CODE LIKE '%' || #{deptCode} || '%'
AND A.PYMNT_SN = #{pymntSn}
AND A.HFFS_SE = #{hffsSe}

mapper.update:
/* kr.co.dbvision.api.mps.cal.mpscal016.service.mapper.Mpscal016Mapper.saveMpscal016 */ MERGE INTO MPS_SALARY_BNDE_REGIST A USING (SELECT #{empno} EMPNO, #{salarytyCode} SALARYTY_CODE, #{salaryitemCode} SALARYITEM_CODE, REPLACE(#{applcYm}, '-', '') APPLC_YM, #{pymntSn} PYMNT_SN FROM DUAL) B ON ( A.EMPNO = B.EMPNO AND A.SALARYTY_CODE = B.SALARYTY_CODE AND A.SALARYITEM_CODE = B.SALARYITEM_CODE AND A.APPLC_YM = B.APPLC_YM AND A.PYMNT_SN = B.PYMNT_SN ) WHEN MATCHED THEN UPDATE SET
PYMNT_AMT= #{pymntAmt}, RM= #{rm}, APPLC_AT= #{applcAt}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
EMPNO, SALARYTY_CODE, SALARYITEM_CODE, APPLC_YM, PYMNT_SN, PYMNT_AMT, RM, APPLC_AT, REG_DT, REG_ID
) VALUES (
#{empno}, #{salarytyCode}, #{salaryitemCode}, REPLACE(#{applcYm}, '-', ''), #{pymntSn}, #{pymntAmt}, #{rm}, #{applcAt}, SYSDATE, #{regId} )

mapper.delete:
