mapper.update:
/* kr.co.dbvision.api.pjt.pmg.pjtpmg002.service.impl.Pjtpmg002Mapper.updatePjtProject */ UPDATE PJT_HNF_PLAN SET PRTCPNT_NM = #{prtcpntNm}, PRTCPNT_EMPNO = #{prtcpntEmpno} , TCHNLGY_GRAD = #{tchnlgyGrad}, ROLE_CODE = #{roleCode}, PARTCPTN_MAN_MONTH = #{partcptnManMonth}, EXTRL_SERVC_AT = #{extrlServcAt} , PARTCPTN_BEGIN_DE = #{partcptnBeginDe}, PARTCPTN_END_DE = #{partcptnEndDe}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
AND PROJECT_SN = #{projectSn}
AND HNF_PLAN_SN = #{hnfPlanSn}

mapper.delete:
/* kr.co.dbvision.api.pjt.pmg.pjtpmg002.service.impl.Pjtpmg002Mapper.deletePjtProject */
DELETE FROM PJT_HNF_PLAN


       AND HNF_PLAN_SN = #{hnfPlanSn}
       AND PROJECT_SN = #{projectSn}

mapper.select:
/* kr.co.dbvision.api.pjt.pmg.pjtpmg002.service.impl.Pjtpmg002Mapper.selectPjtProjectHnfAcmsltList */ SELECT * FROM ( SELECT SUBSTR(ACMSLT_STDR_DE, 1, 4) || '-' || SUBSTR(ACMSLT_STDR_DE, 5, 2) AS ACMSLT_STDR_DE, PARTCPTN_MAN_MONTH , HNF_ACMSLT_SN , TCHNLGY_GRAD , PROJECT_SN , PRTCPNT_NM , PRTCPNT_EMPNO , ROLE_CODE , EXTRL_SERVC_AT AS EXTRL ,SUBSTR(ACMSLT_STDR_DE, 1, 6) AS DIS_ORDER ,TO_NUMBER(''||ROW_NUMBER () over ( order by ACMSLT_STDR_DE DESC, ROLE_CODE ASC, TCHNLGY_GRAD DESC)) AS DIVIDE_CODE FROM PJT_HNF_ACMSLT WHERE PROJECT_SN = #{projectSn}
AND UPPER(PRTCPNT_NM) LIKE '%' || UPPER(#{prtcpntNm}) || '%'
AND ACMSLT_STDR_DE BETWEEN REPLACE(TO_CHAR(TO_DATE(#{searchSregDt}),'YYYY-MM'), '-', '') AND REPLACE(TO_CHAR(TO_DATE(#{searchEregDt}),'YYYY-MM'), '-', '')
UNION SELECT '소계' ACMSLT_STDR_DE , SUM (PARTCPTN_MAN_MONTH) PARTCPTN_MAN_MONTH, '' HNF_ACMSLT_SN , '' TCHNLGY_GRAD , PROJECT_SN , '' PRTCPNT_NM , '' PRTCPNT_EMPNO , '' ROLE_CODE , '0' EXTRL_SERVC_AT ,SUBSTR(ACMSLT_STDR_DE, 1, 6) AS DIS_ORDER ,TO_NUMBER('88') AS DIVIDE_CODE FROM PJT_HNF_ACMSLT WHERE PROJECT_SN = #{projectSn}
AND UPPER(PRTCPNT_NM) LIKE '%' || UPPER(#{prtcpntNm}) || '%'
AND ACMSLT_STDR_DE BETWEEN REPLACE(TO_CHAR(TO_DATE(#{searchSregDt}),'YYYY-MM'), '-', '') AND REPLACE(TO_CHAR(TO_DATE(#{searchEregDt}),'YYYY-MM'), '-', '')
GROUP BY SUBSTR(ACMSLT_STDR_DE, 1, 6) , PROJECT_SN UNION SELECT '합계' ACMSLT_STDR_DE , SUM (PARTCPTN_MAN_MONTH) PARTCPTN_MAN_MONTH, '' HNF_ACMSLT_SN , '' TCHNLGY_GRAD , '' PROJECT_SN , '' PRTCPNT_NM , '' PRTCPNT_EMPNO , '' ROLE_CODE , '0' EXTRL_SERVC_AT ,SUBSTR(ACMSLT_STDR_DE, 1, 1) AS DIS_ORDER -- ,'000000' AS DIS_ORDER ,TO_NUMBER('99') AS DIVIDE_CODE FROM PJT_HNF_ACMSLT WHERE PROJECT_SN = #{projectSn}
AND UPPER(PRTCPNT_NM) LIKE '%' || UPPER(#{prtcpntNm}) || '%'
AND ACMSLT_STDR_DE BETWEEN REPLACE(TO_CHAR(TO_DATE(#{searchSregDt}),'YYYY-MM'), '-', '') AND REPLACE(TO_CHAR(TO_DATE(#{searchEregDt}),'YYYY-MM'), '-', '')
GROUP BY SUBSTR(ACMSLT_STDR_DE, 1, 1) ) ORDER BY DIS_ORDER DESC,DIVIDE_CODE

SELECT ROWNUM AS RNUM, A.* FROM ( SELECT SUBSTR(ACMSLT_STDR_DE, 1, 4) || '-' || SUBSTR(ACMSLT_STDR_DE, 5, 2) AS ACMSLT_STDR_DE, PARTCPTN_MAN_MONTH , HNF_ACMSLT_SN , TCHNLGY_GRAD , PROJECT_SN , PRTCPNT_NM , PRTCPNT_EMPNO , ROLE_CODE , EXTRL_SERVC_AT, UPT_DT, UPT_ID FROM PJT_HNF_ACMSLT WHERE PROJECT_SN = #{projectSn}
AND UPPER(PRTCPNT_NM) LIKE '%' || UPPER(#{prtcpntNm}) || '%'
AND ACMSLT_STDR_DE BETWEEN REPLACE(TO_CHAR(TO_DATE(#{searchSregDt}),'YYYY-MM'), '-', '') AND REPLACE(TO_CHAR(TO_DATE(#{searchEregDt}),'YYYY-MM'), '-', '')
ORDER BY ACMSLT_STDR_DE DESC, ROLE_CODE, TCHNLGY_GRAD DESC, EXTRL_SERVC_AT, HNF_ACMSLT_SN
) A

mapper.update:
        /* kr.co.dbvision.api.pjt.pmg.pjtpmg002.service.impl.Pjtpmg002Mapper.updatePjtProject */
        UPDATE PJT_HNF_ACMSLT SET
			PRTCPNT_NM = #{prtcpntNm},
			PRTCPNT_EMPNO = #{prtcpntEmpno} ,
			TCHNLGY_GRAD = #{tchnlgyGrad},
			ROLE_CODE = #{roleCode},
			PARTCPTN_MAN_MONTH = #{partcptnManMonth},
			EXTRL_SERVC_AT = #{extrlServcAt} ,
			ACMSLT_STDR_DE = #{acmsltStdrDe},
            UPT_DT = SYSDATE,
            UPT_ID = #{uptId}


               AND PROJECT_SN = #{projectSn}


               AND HNF_ACMSLT_SN = #{hnfAcmsltSn}

mapper.delete:
/* kr.co.dbvision.api.pjt.pmg.pjtpmg002.service.impl.Pjtpmg002Mapper.deletePjtProject */
DELETE FROM PJT_HNF_ACMSLT

       AND HNF_ACMSLT_SN = #{hnfAcmsltSn}
       AND PROJECT_SN = #{projectSn}

 /* kr.co.dbvision.api.pjt.pmg.pjtpmg002.service.impl.Pjtpmg002Mapper.deletePjtProject */
