mapper.select:
/* kr.co.dbvision.api.mhs.hrd.pubwks004.service.mapper.Pubwks004Mapper.UserInformationMhshrd002List */
SELECT USER_ID, EMPNO, (SELECT KOR_NM FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS USER_NM, (SELECT DEPT_KOR_NM FROM MHS_DEPT WHERE DEPT_CODE = (SELECT DEPT_CODE FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO)) AS USER_DEPT_NM, (SELECT DEPT_CODE FROM MHS_EMP B WHERE A.EMPNO = B.EMPNO) AS DEPT_NO
FROM STM_USERS A
AND USER_ID = #{regId}

/* kr.co.dbvision.api.pub.wks.pubwks004.service.mapper.Pubwks004Mapper.selectPubwks004List */ SELECT A.LAYOFF_NO, (SELECT COUNT(*) FROM MHS_LAYOFF WHERE LAYOFF_NO = A.LAYOFF_NO AND
ELCTSCT_SE_SN > A.ELCTSCT_SE_SN ) AS COPY_FLAG, A.ELCTSCT_SE_SN, A.EMPNO, (SELECT C.DEPT_KOR_NM FROM MHS_DEPT C WHERE (SELECT B.DEPT_CODE FROM MHS_EMP B WHERE B.EMPNO=A.EMPNO)=C.DEPT_CODE) AS DEPT_NM, (SELECT B.KOR_NM FROM MHS_EMP B WHERE B.EMPNO=A.EMPNO) AS EMP_NM, SUBSTR(FNC_DATESTR(FNC_DATESTR(A.REQST_DE)), 1, 4)||'-'||SUBSTR(FNC_DATESTR(FNC_DATESTR(A.REQST_DE)), 6, 2)||'-'||SUBSTR(FNC_DATESTR(A.REQST_DE), 9, 2) AS REQST_DE, SUBSTR(FNC_DATESTR(A.LAYOFF_BEGIN_DE), 1, 4)||'-'||SUBSTR(FNC_DATESTR(A.LAYOFF_BEGIN_DE), 6, 2)||'-'||SUBSTR(FNC_DATESTR(A.LAYOFF_BEGIN_DE), 9, 2) AS LAYOFF_BEGIN_DE, SUBSTR(FNC_DATESTR(A.LAYOFF_END_DE), 1, 4)||'-'||SUBSTR(FNC_DATESTR(A.LAYOFF_END_DE), 6, 2)||'-'||SUBSTR(FNC_DATESTR(A.LAYOFF_END_DE), 9, 2) AS LAYOFF_END_DE, (A.LAYOFF_DAYCNT||'일') AS LAYOFF_DAYCNT, A.LAYOFF_DTLS, A.EXTN_LAYOFF_NO, A.LAYOFF_SE_CODE, FNC_COMCODENM('C190',A.LAYOFF_SE_CODE) AS LAYOFF_SE_CODE_NM, A.CNWKPD_INCLS_AT, A.ATCHMNFL_NO, A.ELCTSCT_DOC_NO, A.ELCTSCT_STTUS_CODE, FNC_COMCODENM('EA004',A.ELCTSCT_STTUS_CODE) AS ELCTSCT_STTUS_CODE_NM, A.ELCTSCT_EMPNO, A.REG_DT, A.REG_ID, A.UPT_DT, A.UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_LAYOFF A, MHS_EMP B
A.EMPNO=B.EMPNO AND A.EMPNO = (SELECT EMPNO FROM STM_USERS B WHERE B.USER_ID = #{regId})
AND B.DEPT_CODE = #{deptCode}
AND C.DEPT_KOR_NM = #{deptCodeNm}
AND A.EMPNO = #{empno}
AND B.KOR_NM = #{empCodeNm}
AND A.LAYOFF_SE_CODE = #{layoffSeCode}
AND A.ELCTSCT_STTUS_CODE = #{elctsctSttusCode}
AND ((A.LAYOFF_BEGIN_DE >= #{layoffBeginDe} OR A.LAYOFF_END_DE >= #{layoffBeginDe}) AND (A.LAYOFF_BEGIN_DE <= #{layoffEndDe} OR A.LAYOFF_END_DE <= #{layoffEndDe}))
ORDER BY LAYOFF_NO DESC , ELCTSCT_SE_SN

/* kr.co.dbvision.api.pub.wks.pubwks004.service.mapper.Pubwks004Mapper.selectPubwks004 */ SELECT LAYOFF_NO, ELCTSCT_SE_SN, EMPNO, REQST_DE, LAYOFF_BEGIN_DE, LAYOFF_END_DE, LAYOFF_DAYCNT, LAYOFF_DTLS, EXTN_LAYOFF_NO, LAYOFF_SE_CODE, CNWKPD_INCLS_AT, ATCHMNFL_NO, ELCTSCT_DOC_NO, ELCTSCT_STTUS_CODE, ELCTSCT_EMPNO, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_LAYOFF A
AND LAYOFF_NO = #{layoffNo}
AND ELCTSCT_SE_SN = #{elctsctSeSn}

mapper.update:
/* kr.co.dbvision.api.pub.wks.pubwks004.service.mapper.Pubwks004Mapper.savePubwks004 */ MERGE INTO MHS_LAYOFF A USING (SELECT #{layoffNo} LAYOFF_NO, #{elctsctSeSn} ELCTSCT_SE_SN FROM DUAL) B ON ( A.LAYOFF_NO = B.LAYOFF_NO AND A.ELCTSCT_SE_SN = B.ELCTSCT_SE_SN ) WHEN MATCHED THEN UPDATE SET
EMPNO= #{empno}, REQST_DE= REPLACE(#{reqstDe}, '-', ''), LAYOFF_BEGIN_DE= REPLACE(#{layoffBeginDe}, '-', ''), LAYOFF_END_DE= REPLACE(#{layoffEndDe}, '-', ''), LAYOFF_DAYCNT= REPLACE(#{layoffDaycnt}, '일', ''), LAYOFF_DTLS= #{layoffDtls}, EXTN_LAYOFF_NO= #{extnLayoffNo}, LAYOFF_SE_CODE= #{layoffSeCode}, CNWKPD_INCLS_AT= #{cnwkpdInclsAt}, ATCHMNFL_NO= #{atchmnflNo}, ELCTSCT_DOC_NO= #{elctsctDocNo}, ELCTSCT_STTUS_CODE= #{elctsctSttusCode}, ELCTSCT_EMPNO= #{elctsctEmpno}, UPT_DT = SYSDATE, UPT_ID = #{userId}
WHEN NOT MATCHED THEN INSERT (
LAYOFF_NO, ELCTSCT_SE_SN, EMPNO, REQST_DE, LAYOFF_BEGIN_DE, LAYOFF_END_DE, LAYOFF_DAYCNT, LAYOFF_DTLS, EXTN_LAYOFF_NO, LAYOFF_SE_CODE, CNWKPD_INCLS_AT, ATCHMNFL_NO, ELCTSCT_DOC_NO, ELCTSCT_STTUS_CODE, ELCTSCT_EMPNO, REG_DT, REG_ID
) VALUES (
#{layoffNo}, '1', #{empno}, REPLACE(#{reqstDe}, '-', ''), REPLACE(#{layoffBeginDe}, '-', ''), REPLACE(#{layoffEndDe}, '-', ''), REPLACE(#{layoffDaycnt}, '일', ''), #{layoffDtls}, #{extnLayoffNo}, #{layoffSeCode}, #{cnwkpdInclsAt}, #{atchmnflNo}, #{elctsctDocNo}, #{elctsctSttusCode}, #{elctsctEmpno}, SYSDATE, #{userId} )

mapper.delete:
/* kr.co.dbvision.api.pub.wks.pubwks004.service.mapper.Pubwks004Mapper.deletePubwks004 */
DELETE FROM MHS_LAYOFF


        AND LAYOFF_NO = #{layoffNo}
        AND ELCTSCT_SE_SN = #{elctsctSeSn}

mapper.select:
/* kr.co.dbvision.api.pub.wks.pubwks004.service.mapper.Pubwks004Mapper.searchPubwks004ForExcel */ SELECT A.LAYOFF_NO, SUBSTR(FNC_DATESTR(FNC_DATESTR(A.REQST_DE)), 1, 4)||'-'||SUBSTR(FNC_DATESTR(FNC_DATESTR(A.REQST_DE)), 6, 2)||'-'||SUBSTR(FNC_DATESTR(A.REQST_DE), 9, 2) AS REQST_DE, (SELECT C.DEPT_KOR_NM FROM MHS_DEPT C WHERE (SELECT B.DEPT_CODE FROM MHS_EMP B WHERE B.EMPNO=A.EMPNO)=C.DEPT_CODE) AS DEPT_KOR_NM, A.EMPNO, (SELECT B.KOR_NM FROM MHS_EMP B WHERE B.EMPNO=A.EMPNO) AS EMP_NM, A.LAYOFF_SE_CODE, FNC_COMCODENM('C190',A.LAYOFF_SE_CODE) AS LAYOFF_SE_CODE_NM, SUBSTR(FNC_DATESTR(A.LAYOFF_BEGIN_DE), 1, 4)||'-'||SUBSTR(FNC_DATESTR(A.LAYOFF_BEGIN_DE), 6, 2)||'-'||SUBSTR(FNC_DATESTR(A.LAYOFF_BEGIN_DE), 9, 2) AS LAYOFF_BEGIN_DE, SUBSTR(FNC_DATESTR(A.LAYOFF_END_DE), 1, 4)||'-'||SUBSTR(FNC_DATESTR(A.LAYOFF_END_DE), 6, 2)||'-'||SUBSTR(FNC_DATESTR(A.LAYOFF_END_DE), 9, 2) AS LAYOFF_END_DE, (A.LAYOFF_DAYCNT||'일') AS LAYOFF_DAYCNT, A.LAYOFF_DTLS, A.ATCHMNFL_NO, A.ELCTSCT_SE_SN, A.EXTN_LAYOFF_NO, A.CNWKPD_INCLS_AT, A.ELCTSCT_DOC_NO, A.ELCTSCT_STTUS_CODE, FNC_COMCODENM('EA004',A.ELCTSCT_STTUS_CODE) AS ELCTSCT_STTUS_CODE_NM, A.ELCTSCT_EMPNO, A.REG_DT, A.REG_ID, A.UPT_DT, A.UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM MHS_LAYOFF A, MHS_EMP B
A.EMPNO=B.EMPNO AND A.EMPNO = #{empno}
AND ((A.LAYOFF_BEGIN_DE >= #{layoffBeginDe} OR A.LAYOFF_END_DE >= #{layoffBeginDe}) AND (A.LAYOFF_BEGIN_DE <= #{layoffEndDe} OR A.LAYOFF_END_DE <= #{layoffEndDe}))
AND A.LAYOFF_SE_CODE = #{layoffSeCode}
AND A.ELCTSCT_STTUS_CODE = #{elctsctSttusCode}
AND A.REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND A.REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')
ORDER BY REG_DT DESC
