/* kr.co.dbvision.api.pub.wks.pubwks013.service.mapper.Pubwks013Mapper.userDataPubwks013 */ SELECT EMPNO, KOR_NM, DEPT_CODE, BPLC_CODE, (SELECT C.DEPT_KOR_NM FROM MHS_DEPT C WHERE C.DEPT_CODE = A.DEPT_CODE) AS DEPT_NM
FROM MHS_EMP A
AND EMPNO = (SELECT B.EMPNO FROM STM_USERS B WHERE B.USER_ID = #{regId})

SELECT
    COUNT(*)
FROM
    MHS_BSRP_EXCCLC
WHERE
    BSRP_NO = #{bsrpNo}
    AND
    ELCTSCT_SE_SN = #{elctsctSeSn}
    AND

    EXCCLC_SN > #{excclcSn}
