mapper.select:
/* kr.co.dbvision.api.pub.wks.pubwks016.service.mapper.Pubwks016Mapper.userDataPubwks016 */ SELECT EMPNO, KOR_NM, DEPT_CODE, BPLC_CODE, (SELECT C.DEPT_KOR_NM FROM MHS_DEPT C WHERE C.DEPT_CODE = A.DEPT_CODE) AS DEPT_NM
FROM MHS_EMP A
AND EMPNO = (SELECT B.EMPNO FROM STM_USERS B WHERE B.USER_ID = #{regId})

mapper.update:
/* kr.co.dbvision.api.pub.wks.pubwks016.service.mapper.Pubwks016Mapper.savePubwks016 */ MERGE INTO MHS_BSRP A USING (SELECT #{bsrpNo} BSRP_NO , #{elctsctSeSn} ELCTSCT_SE_SN FROM DUAL) B ON ( A.BSRP_NO = B.BSRP_NO AND A.ELCTSCT_SE_SN = B.ELCTSCT_SE_SN ) WHEN MATCHED THEN UPDATE SET
BPLC_CODE= #{bplcCode}, REQST_DE= REPLACE(#{reqstDe},'-',''), BSRP_NM= #{bsrpNm}, BSRP_PURPS= #{bsrpPurps}, BSRP_SE_CODE= #{bsrpSeCode}, REQST_EMPNO= #{reqstEmpno}, BSRP_SDT= #{bsrpSdt}, BSRP_EDT= #{bsrpEdt}, BSRP_SHR= REPLACE(#{bsrpShr},':',''), BSRP_EHR= REPLACE(#{bsrpEhr},':',''), TRNSPORT_SE_CODE= #{trnsportSeCode}, STAYNG= #{stayng}, STAYNG_DAYCNT= #{stayngDaycnt}, BUGT_CODE= #{bugtCode}, ELCTSCT_DOC_NO= #{elctsctDocNo}, ELCTSCT_EMPNO= #{elctsctEmpno}, UPT_DT = SYSDATE, UPT_ID = #{uptId}, BSRP_CITY = #{bsrpCity}
WHEN NOT MATCHED THEN INSERT (
BSRP_NO, ELCTSCT_SE_SN, BPLC_CODE, REQST_DE, BSRP_NM, BSRP_PURPS, BSRP_SE_CODE, REQST_EMPNO, BSRP_SDT, BSRP_EDT, BSRP_SHR, BSRP_EHR, TRNSPORT_SE_CODE, STAYNG, STAYNG_DAYCNT, BUGT_CODE, ELCTSCT_DOC_NO, ELCTSCT_STTUS_CODE, ELCTSCT_EMPNO, REG_DT, REG_ID, BSRP_CITY
) VALUES (
#{bsrpNo}, (SELECT NVL(MAX(ELCTSCT_SE_SN) , 0) + 1 FROM MHS_BSRP WHERE BSRP_NO = #{bsrpNo}), #{bplcCode}, REPLACE(#{reqstDe},'-',''), #{bsrpNm}, #{bsrpPurps}, #{bsrpSeCode}, #{reqstEmpno}, #{bsrpSdt}, #{bsrpEdt}, REPLACE(#{bsrpShr},':',''), REPLACE(#{bsrpEhr},':',''), #{trnsportSeCode}, #{stayng}, #{stayngDaycnt}, #{bugtCode}, #{elctsctDocNo}, #{elctsctSttusCode}, #{elctsctEmpno}, SYSDATE, #{regId}, #{bsrpCity}
)

/* kr.co.dbvision.api.pub.wks.pubwks016.service.mapper.Pubwks016Mapper.saveDtlPubwks016 */ MERGE INTO MHS_BSRP_DETAIL A USING (SELECT #{bsrpNo} BSRP_NO, #{bsrpEmpno} BSRP_EMPNO, #{elctsctSeSn} ELCTSCT_SE_SN, ELCTSCT_STTUS_CODE FROM MHS_BSRP WHERE BSRP_NO = #{bsrpNo} AND ELCTSCT_SE_SN = #{elctsctSeSn}) B ON ( A.BSRP_NO = B.BSRP_NO AND A.BSRP_EMPNO = B.BSRP_EMPNO AND A.ELCTSCT_SE_SN = B.ELCTSCT_SE_SN AND (B.ELCTSCT_STTUS_CODE IS NULL OR B.ELCTSCT_STTUS_CODE = '40') ) WHEN MATCHED THEN UPDATE SET
VISIT_OFFIC_NM = #{visitOfficNm}, VISIT_CN = #{visitCn}, DAY_AMT = #{dayAmt}, CGFFD_AMT = #{cgffdAmt}, STAYNG_AMT = #{stayngAmt}, TRNSPORT_AMT = #{trnsportAmt}, LOCAL_TRNSPORT_AMT = #{localTrnsportAmt}, DTY_VRSC_EMPNO = #{dtyVrscEmpno}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
BSRP_NO, BSRP_EMPNO, ELCTSCT_SE_SN, VISIT_OFFIC_NM, VISIT_CN, DAY_AMT, CGFFD_AMT, STAYNG_AMT, TRNSPORT_AMT, LOCAL_TRNSPORT_AMT, DTY_VRSC_EMPNO, REG_DT, REG_ID
) VALUES (
#{bsrpNo}, #{bsrpEmpno}, #{elctsctSeSn}, #{visitOfficNm}, #{visitCn}, #{dayAmt}, #{cgffdAmt}, #{stayngAmt}, #{trnsportAmt}, #{localTrnsportAmt}, #{dtyVrscEmpno}, SYSDATE, #{regId}
)

mapper.delete:
/* kr.co.dbvision.api.pub.wks.pubwks016.service.mapper.Pubwks016Mapper.deletePubwks016 */
DELETE FROM MHS_BSRP


        AND BSRP_NO = #{bsrpNo}
        AND ELCTSCT_SE_SN = #{elctsctSeSn}

/* kr.co.dbvision.api.pub.wks.pubwks016.service.mapper.Pubwks016Mapper.deleteDtlPubwks016 */
DELETE FROM MHS_BSRP_DETAIL


        AND BSRP_NO = #{bsrpNo}
        AND ELCTSCT_SE_SN = #{elctsctSeSn}

/* kr.co.dbvision.api.pub.wks.pubwks016.service.mapper.Pubwks016Mapper.deleteDtlEmpPubwks016 */
DELETE FROM MHS_BSRP_DETAIL


        AND BSRP_NO = #{bsrpNo}
        AND BSRP_EMPNO = #{bsrpEmpno}
        AND ELCTSCT_SE_SN = #{elctsctSeSn}
