mapper.select:
/* kr.co.dbvision.api.stm.mng.stmmng003.service.mapper.Stmmng003Mapper.selectStmmng003 */ SELECT CODEKIND_CODE, CODEKIND_NM, CODE_LT, SYS_SE, CODE_DC, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM STM_CODEKIND A
AND CODEKIND_CODE = #{codekindCode}

mapper.update:
/* kr.co.dbvision.api.stm.mng.stmmng003.service.mapper.Stmmng003Mapper.saveStmmng003 */ MERGE INTO STM_CODEKIND A USING (SELECT #{codekindCode} CODEKIND_CODE FROM DUAL) B ON ( A.CODEKIND_CODE = B.CODEKIND_CODE ) WHEN MATCHED THEN UPDATE SET
CODEKIND_NM= #{codekindNm}, CODE_LT= #{codeLt}, SYS_SE= #{sysSe}, CODE_DC= #{codeKindDc}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
CODEKIND_CODE, CODEKIND_NM, CODE_LT, SYS_SE, CODE_DC, REG_DT, REG_ID
) VALUES (
#{codekindCode}, #{codekindNm}, #{codeLt}, #{sysSe}, #{codeKindDc}, SYSDATE, #{regId}
)

mapper.select:
/* kr.co.dbvision.api.stm.mng.stmmng003.service.mapper.Stmmng003Mapper.selectCodeStmmng003List */ SELECT CODE, CODEKIND_CODE AS CODEKIND, CODE_KOR_NM, CODE_ENG_NM, CODE_DC, ORDR, USE_AT, DFLT_AT, FACTOR, REFER1_DC, REFER2_DC, REFER3_DC, REFER4_DC, CODE_THIRD_NM, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM STM_CODE A
AND CODE = #{code}
AND CODEKIND_CODE = #{codekindCode}
AND REG_DT >= TO_DATE(#{sRegDt} || '000000','YYYY-MM-DDHH24MISS')
AND REG_DT <= TO_DATE(#{eRegDt} || '235959','YYYY-MM-DDHH24MISS')
ORDER BY ORDR

/* kr.co.dbvision.api.stm.mng.stmmng003.service.mapper.Stmmng003Mapper.selectCodeStmmng003 */ SELECT CODE, CODEKIND_CODE, CODE_KOR_NM, CODE_ENG_NM, CODE_DC, ORDR, USE_AT, DFLT_AT, FACTOR, REFER1_DC, REFER2_DC, REFER3_DC, REFER4_DC, CODE_THIRD_NM, REG_DT, REG_ID, UPT_DT, UPT_ID, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.REG_ID) AS REG_NM, (SELECT USER_NM FROM STM_USERS WHERE USER_ID = A.UPT_ID) AS UPT_NM
FROM STM_CODE A
AND CODE = #{code}
AND CODEKIND_CODE = #{codekind}

mapper.update:
/* kr.co.dbvision.api.stm.mng.stmmng003.service.mapper.Stmmng003Mapper.saveCodeStmmng003 */ MERGE INTO STM_CODE A USING (SELECT #{code} CODE, #{codekind} CODEKIND_CODE FROM DUAL) B ON ( A.CODE = B.CODE AND A.CODEKIND_CODE = B.CODEKIND_CODE ) WHEN MATCHED THEN UPDATE SET
CODE_KOR_NM= #{codeKorNm}, CODE_ENG_NM= #{codeEngNm}, CODE_DC= #{codeDc}, ORDR= #{ordr}, USE_AT= #{useAt}, DFLT_AT= #{dfltAt}, FACTOR= #{factor}, REFER1_DC= #{refer1Dc}, REFER2_DC= #{refer2Dc}, REFER3_DC= #{refer3Dc}, REFER4_DC= #{refer4Dc}, CODE_THIRD_NM= #{codeThirdNm}, UPT_DT = SYSDATE, UPT_ID = #{uptId}
WHEN NOT MATCHED THEN INSERT (
CODE, CODEKIND_CODE, CODE_KOR_NM, CODE_ENG_NM, CODE_DC, ORDR, USE_AT, DFLT_AT, FACTOR, REFER1_DC, REFER2_DC, REFER3_DC, REFER4_DC, CODE_THIRD_NM, REG_DT, REG_ID
) VALUES (
#{code}, #{codekind}, #{codeKorNm}, #{codeEngNm}, #{codeDc}, #{ordr}, #{useAt}, #{dfltAt}, #{factor}, #{refer1Dc}, #{refer2Dc}, #{refer3Dc}, #{refer4Dc}, #{codeThirdNm}, SYSDATE, #{regId}
)

mapper.delete:
/* kr.co.dbvision.api.stm.mng.stmmng003.service.mapper.Stmmng003Mapper.deleteCodeStmmng003 */
DELETE FROM STM_CODE


        AND CODE = #{code}
        AND CODEKIND_CODE = #{codekind}

mapper.select:
/* kr.co.dbvision.api.stm.mng.stmmng003.service.impl.Stmmng003Mapper.selectStmCodeListAll */ SELECT CODE, CODE_KOR_NM, ORDR, DECODE(USE_AT, '1', '사용', '0', '미사용', '미사용') AS USE_AT, CODEKIND_CODE FROM STM_CODE
AND CODEKIND_CODE = #{codekindCode}
AND USE_AT = #{useAt}
AND CODE_KOR_NM LIKE '%' || #{codeKorNm} || '%'
ORDER BY CODEKIND_CODE,CODE
